mapper.update:
UPDATE process_step_info
SET lstc_file_path = #{lstc_file_path},
    fie_name = #{originalname}
WHERE prj_id = #{prj_id}
AND version_number = #{version_number}
AND step_number = #{step_number}

mapper.insert:
INSERT INTO process_comment
(
    comm_id
  , prj_id
  , version_number
  , rgst_user_id
  , step_number
  , comment_description
)
VALUES
(
    #{comm_id}
  , #{prj_id}
  , #{version_number}
  , #{rgst_user_id}
  , #{step_number}
  , #{comment_description}
)

INSERT INTO process_comment_file
(
    comm_file_id
  , comm_id
  , file_path
  , file_name
)
VALUES
(
    #{comm_file_id}
  , #{comm_id}
  , #{file_path}
  , #{file_name}
)

mapper.select:
SELECT a.comm_id , a.prj_id , a.version_number , a.rgst_user_id , b.user_name , CASE WHEN b.user_level = 3 THEN '개발담당자' WHEN b.user_level = 2 THEN '보안담당자' WHEN b.user_level = 1 THEN '시스템관리자' ELSE '' END AS user_role , a.step_number , a.comment_description , a.updt_dtm FROM process_comment a, user_info b WHERE a.prj_id = #{prj_id} AND a.version_number = #{version_number}
AND a.step_number = #{step_number}
AND a.rgst_user_id = b.user_id ORDER BY a.comm_id ASC;

mapper.update:
UPDATE process_comment
SET comment_description = #{comment_description}
WHERE comm_id = #{comm_id}

mapper.delete:
DELETE FROM process_step_info_file
WHERE prj_id = #{prj_id}
AND version_number = #{version_number}
AND step_number = #{step_number}

mapper.select:
SELECT  step_id             
      , prj_id             
      , version_number     
      , step_number
FROM  project_step
WHERE prj_id = #{prj_id}
AND version_number = #{version_number}
AND step_number = #{step_number}

mapper.update:
UPDATE process_step_info


        step_lnk = #{step_lnk}


        , step_description = #{step_description}


        , step_status = 'A'


WHERE prj_id = #{prj_id}
AND version_number = #{version_number}
AND step_number = #{step_number}

